INSERT INTO mc_page_langs VALUES('5','3','1','Welcome To our Merchant Center','&lt;h2&gt;We do not drive traffic, We drive shoppers Shoppers! We help your product reach millions of customers each day boosting your sales&lt;/h2&gt;\r\n&lt;h3&gt;It&amp;rsquo;s very simple and easy just at your finger tips!&lt;/h3&gt;','[{\"slug\":\"inner_points\",\"key\":\"Upload Feed Easily\",\"subtitle\":\"Get heavy sales traffic and make proffit\",\"values\":\"By listing your products as a vendor, you will reach you target audience, promote your offers to attract qualified shoppers and generate traffic and eyeball to your store.\",\"img\":\"uploads\\/page\\/attr\\/mrchnt1.png\"},{\"slug\":\"inner_points\",\"key\":\"Manage Store in Finger Tips\",\"subtitle\":\"Manage your store products any time from any where\",\"values\":\"Manage your store and products very quickly at Menacompare. Customize your Products, Brands and track product search, browsing, intrest, sales easily.\",\"img\":\"uploads\\/page\\/attr\\/mrchnt2.png\"},{\"slug\":\"inner_points\",\"key\":\"Increase Sales Traffic\",\"subtitle\":\"Get heavy sales traffic and make proffit\",\"values\":\"By listing your products as a vendor, you will reach you target audience, promote your offers to attract qualified shoppers and generate traffic and eyeball to your store.Moreover\",\"img\":\"uploads\\/page\\/attr\\/mrchnt3.png\"},{\"slug\":\"tagline\",\"key\":\"Easy to setup and use\",\"subtitle\":\"\",\"values\":\"\",\"img\":\"uploads\\/page\\/attr\\/merchant_06-06.png\"},{\"slug\":\"tagline\",\"key\":\"Expand your business and grow your customer base.\",\"subtitle\":\"\",\"values\":\"\",\"img\":\"uploads\\/page\\/attr\\/merchant_06-13.png\"},{\"slug\":\"tagline\",\"key\":\"Promote your offers to ready-to-buy shoppers\",\"subtitle\":\"\",\"values\":\"\",\"img\":\"uploads\\/page\\/attr\\/merchant_06-20.png\"},{\"slug\":\"tagline\",\"key\":\"Reach your exact target audience\",\"subtitle\":\"\",\"values\":\"\",\"img\":\"uploads\\/page