  `contact_email` varchar(50) NOT NULL,
  `contact_phone` varchar(20) NOT NULL,
  `shipping_details` varchar(100) NOT NULL,
  `shipping_price` float NOT NULL,
  `shipping_time` varchar(222) NOT NULL,
  `payment_details` varchar(200) NOT NULL COMMENT 'which type of payment available for merchant(store in json format by coma separate )',
  `status` enum('1','0') NOT NULL DEFAULT '0',
  `create_date` datetime NOT NULL,
  `last_modified` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=26 DEFAULT CHARSET=latin1;




DROP TABLE IF EXISTS mc_products;

CREATE TABLE `mc_products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `merchant_product_id` int(11) NOT NULL,
  `slug` varchar(200) NOT NULL,
  `product_url` varchar(300) NOT NULL,
  `category_id` bigint(20) DEFAULT NULL,
  `retailer_id` bigint(11) NOT NULL,
  `sku` varchar(50) NOT NULL,
  `price` double NOT NULL,
  `price_type` varchar(10) NOT NULL,
  `image_url` text NOT NULL,
  `manufacturer` varchar(150) NOT NULL,
  `brand` varchar(150) DEFAULT NULL,
  `department` varchar(150) NOT NULL,
  `upc` varchar(50) DEFAULT NULL,
  `mpn` varchar(50) DEFAULT NULL,
  `isbn` varchar(50) DEFAULT NULL,
  `color` varchar(50) DEFAULT NULL,
  `shipping_name` varchar(222) NOT NULL,
  `shipping_cost` double NOT NULL,
  `shipping_weight` float NOT NULL,
  `weight` float DEFAULT NULL,
  `height` float DEFAULT NULL,
  `width` float DEFAULT NULL,
  `product_type` varchar(50) NOT NULL,
  `quantity` int(11) NOT NULL,
  `availability` varchar(20) NOT NULL,
  `condition` varchar(20) NOT NULL,
  `tax` varchar(20) NOT NULL,
  `bid` varchar(20) NOT NULL,
  `promotion_code` varchar(20) NOT NULL,
  `status` enum('1','0') NOT NULL DEFAULT '1',
  `deleted` enum('1','0') NOT NULL DEFAULT '0',
  `created_date` datetime NOT NULL,
  `last_modified` datetime NOT NULL,
  `offer_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  FULLTEXT KEY `slug` (`slug`)
) ENGINE=MyISAM AUTO_INCREMENT=1281 DEFAULT CHARSET=latin1;




