  `currency` varchar(10) CHARACTER SET utf8 NOT NULL,
  `retailer_id` bigint(11) NOT NULL,
  `model_no` varchar(100) CHARACTER SET utf8 NOT NULL,
  `sku` varchar(50) CHARACTER SET utf8 NOT NULL,
  `price` double NOT NULL,
  `price_type` varchar(10) NOT NULL,
  `image_url` text NOT NULL,
  `manufacturer` varchar(150) NOT NULL,
  `brand` varchar(150) DEFAULT NULL,
  `department` varchar(150) CHARACTER SET utf8 NOT NULL,
