DROP TABLE IF EXISTS mc_users;

CREATE TABLE `mc_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `email` varchar(50) NOT NULL,
  `phone` varchar(20) NOT NULL,
  `username` varchar(50) NOT NULL,
  `password` varchar(100) NOT NULL,
  `user_type` enum('A','U','R') NOT NULL COMMENT 'A = Admin, U = User, R = Retailer',
  `status` enum('1','0') NOT NULL DEFAULT '0' COMMENT '1 = Active, 0 = Inactive',
  `activation_code` varchar(200) NOT NULL,
